package com.mirai.importback.service;

import com.mirai.importback.entities.ProductList;
import com.mirai.importback.entities.StoreProducts;
import com.mirai.importback.entities.Users;
import com.mirai.importback.repositories.IProductListRepository;
import com.mirai.importback.services.impl.ProductListImpl;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.junit.jupiter.MockitoExtension;

import java.util.ArrayList;
import java.util.List;
import java.util.Optional;

import static org.assertj.core.api.Assertions.assertThat;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.mockito.BDDMockito.given;
import static org.mockito.Mockito.times;
import static org.mockito.Mockito.verify;

@ExtendWith(MockitoExtension.class)
public class ProductListImplTest {


    @Mock
    private IProductListRepository productListRepository;

    @InjectMocks
    private ProductListImpl productListService;

    @Test
    public void saveTest(){


        ProductList productList = new ProductList(1L,"IPHONE","https://encrypted-tbn2.gstatic.com/shopping?q=tbn:ANd9GcT9wpDgBk6aeJ5bSrcFsGgX7-dDw1rZLO-ExAib9OCpalV3m4hG5v53v-x7FrIdlp0gbtQDvkBwfw&usqp=CAc","233",new StoreProducts());

        given(productListRepository.save(productList)).willReturn(productList);

        ProductList savedProductList = null;
        try{
            savedProductList = productListService.save(productList);
        }catch (Exception e){
            e.printStackTrace();
        }
        assertThat(savedProductList).isNotNull();
        assertEquals(productList, savedProductList);
    }

    @Test
    public void deleteTest() throws Exception{
        Long id = 1L;
        productListService.delete(id);
        verify(productListRepository, times(1)).deleteById(id);
    }

    @Test
    public void getAllTest() throws Exception{
        List<ProductList> list = new ArrayList<>();
        list.add(new ProductList(1L,"IPHONE","https://encrypted-tbn2.gstatic.com/shopping?q=tbn:ANd9GcT9wpDgBk6aeJ5bSrcFsGgX7-dDw1rZLO-ExAib9OCpalV3m4hG5v53v-x7FrIdlp0gbtQDvkBwfw&usqp=CAc","238",new StoreProducts()));
        list.add(new ProductList(2L,"TABLET","data:image/png;base64,    ","300",new StoreProducts()));
        list.add(new ProductList(3L,"WATCH","data:image/png;base64,","429",new StoreProducts()));
        list.add(new ProductList(4L,"SUIT","https://encrypted-tbn1.gstatic.com/shopping?q=tbn:ANd9GcRie4Ky5KTFvx8ueu8tQD1fLdBM1MfrShZq1w3lKSiiZrC9uPKeeyUVW09vht4meUCJRWHWjzBXnWo&usqp=CAc","123",new StoreProducts()));
        list.add(new ProductList(5L,"SHOES","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRiPgaFaPA5BJwg4J38_NCgO_gdk3u0ynJdIcD262uCdQ&s","222",new StoreProducts()));

        given(productListRepository.findAll()).willReturn(list);
        List<ProductList> listExpected = productListService.getAll();
        assertEquals(listExpected, list);
    }

    @Test
    public void getByIdTest() throws Exception{
        Long id = 1L;
        ProductList customer = new ProductList(1L,"SUIT","https://encrypted-tbn1.gstatic.com/shopping?q=tbn:ANd9GcRie4Ky5KTFvx8ueu8tQD1fLdBM1MfrShZq1w3lKSiiZrC9uPKeeyUVW09vht4meUCJRWHWjzBXnWo&usqp=CAc","233",new StoreProducts());

        given(productListRepository.findById(id)).willReturn(Optional.of(customer));
        Optional<ProductList> customerExpected = productListService.getById(id);
        assertThat(customerExpected).isNotNull();
        assertEquals(customerExpected, Optional.of(customer));
    }

}
